import React from "react";
import { Link } from "react-router-dom";

const CompanyCard = ({handle, name, description, logoUrl}) => {
  return (
    <Link to={`/companies/${handle}`}>
          <div>
              <h5>{name}</h5>
              <p>{description}</p>
          </div>
    </Link>
  );
}

export default CompanyCard;
