import UserContext from "./auth/UserContext";
import logos from "./logos"
import Loading from "./helpers/Loading";
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [user, setUser] = useState("");

  useEffect(() => {
    async function getItem() {
      setIsLoading(false);
    }
    getItem();
  }, []);

 if (isLoading) {
    return <Loading />
  }

  return (
      <BrowserRouter>
          <div className="App">
            <NavBar />
            <RoutesList />
          </div>
      </BrowserRouter>
  );
}

export default App;
