import React, { useContext } from "react";
import UserContext from "../auth/UserContext";
import JobCard from "../jobs/JobCard";

function Home() {
  const { currentUser } = useContext(UserContext);

  const yourJobs = (
    <ul>
      <i>Jobs</i>
      {currentUser.jobs.map((j) => (
        <li>
          <JobCard
            key={j.id + j.title}
            companyName={j.name}
            title={j.title}
            equity={j.equity}
            salary={j.salary}
          />
        </li>
      ))}
    </ul>
  );

  if (currentUser && currentUser.username !== "testuser") {
    return (
      <div className="container text-center">
        <h3>Welcome to Jobly, {currentUser.username}!</h3>
        <br></br>
        <h6>Jobs You've Applied For:</h6>
        <p>{currentUser.jobs ? yourJobs : "No job applications yet"}</p>
      </div>
    );
  }

  return (
    <div className="container text-center">
      <h3>Welcome to Jobly!</h3>
    </div>
  );
}

export default Home;
