import React, { useContext, useEffect, useState } from "react";
import UserContext from "../auth/UserContext";
import { Link } from "react-router-dom";
import JoblyApi from "../api";
import JobCard from "../jobs/JobCard";

function Home() {
  const { currentUser } = useContext(UserContext);
  const [jobs, setJobs] = useState([]);

  const ids = currentUser.applications;

  useEffect(
    function getJobsforHomePage() {
      async function getJob(id) {
        return await JoblyApi.getJob(id);
      }
      let list = ids.map((j) => getJob(j));
      console.log(list);
      setJobs();
    },
    [ids]
  );

  if (currentUser && currentUser.username !== "testuser") {
    return (
      <div className="container text-center">
        <h3>Welcome to Jobly, {currentUser.firstName}!</h3>
        <br></br>
        <h6>Jobs You've Applied To:</h6>
        <p>{jobs} ?? "No job applications yet"</p>
      </div>
    );
  }

  return (
    <div className="container text-center">
      <h3>Welcome to Jobly!</h3>
      <h6>
        <Link to="/signup">Signup</Link> or <Link to="/login">Login</Link> to
        continue.
      </h6>
    </div>
  );
}

export default Home;
