import React, { useContext, useEffect } from "react";
import UserContext from "../auth/UserContext";
import { Link } from "react-router-dom";
import JoblyApi from "../api";
import JobCard from "../jobs/JobCard";

function Home() {
  const { currentUser } = useContext(UserContext);

  const getJob = async (id) => {
    let job = await JoblyApi.getJob(id);
    return job;
  };

  // get job details and update if jobs changes.
  useEffect(() => {
    currentUser.applications.map((j) => getJob(j));
  }, [currentUser.applications]);

  if (currentUser && currentUser.username !== "testuser") {
    return (
      <div className="container text-center">
        <h3>Welcome to Jobly, {currentUser.firstName}!</h3>
        <br></br>
        <h6>Jobs You've Applied To:</h6>
        <p>{currentUser.applications} ?? "No job applications yet"</p>
      </div>
    );
  }

  return (
    <div className="container text-center">
      <h3>Welcome to Jobly!</h3>
      <h6>
        <Link to="/signup">Signup</Link> or <Link to="/login">Login</Link> to
        continue.
      </h6>
    </div>
  );
}

export default Home;
